package com.es.config.es;import com.es.param.result.EsSubscriptionBasicInfo;import org.elasticsearch.search.SearchHit;import java.util.Map;/** * @author runxiu.li * @Date 2023/4/27 10:00 * @Description TODO */public class SubscriptionConvertor implements SearchHitConvertor<EsSubscriptionBasicInfo> {    private SubscriptionConvertor() {    }    private static volatile SubscriptionConvertor instance;    public static SubscriptionConvertor getInstance() {        if (instance != null) {            return instance;        }        synchronized (SubscriptionConvertor.class) {            if (instance == null) {                instance = new SubscriptionConvertor();            }            return instance;        }    }    @Override    public EsSubscriptionBasicInfo convert(SearchHit searchHit) {        Map<String, Object> source = searchHit.getSourceAsMap();        return new EsSubscriptionBasicInfo();    }}