package com.es.param.result;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;/** * @author yeqing.zong */@Data@NoArgsConstructor@AllArgsConstructorpublic class PageList<T> {    private List<T> list;    private Long count;    private String scrollId;    public static <T> PageList<T> of(Long total, List<T> items) {        PageList pageList = new PageList();        pageList.setCount(total);        pageList.setList(items);        return pageList;    }    public static <T> PageList<T> of(Long total, List<T> items, String scrollId) {        PageList pageList = new PageList();        pageList.setCount(total);        pageList.setScrollId(scrollId);        pageList.setList(items);        return pageList;    }}